*{
    box-sizing: border-box;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
}
body{
    font-family: Helvetica;
    -webkit-font-smoothing: antialiased;
    /*background: rgba( 71, 147, 227, 1);*/
}
h2{
    text-align: center;
    font-size: 18px;
    text-transform: uppercase;
    letter-spacing: 1px;
    color: white;
    padding: 30px 0;
}

/* Table Styles */

.table-container {
    display: inline-block;
    width: auto;
    height: 200px;                  /* Altura do container */
    overflow: auto;
	font-style: normal;
	font-family: arial;verdana;
	font-size: 13px;
	color: #6495ED;
	display:block;
	border-collapse: collapse;
	border: 0px solid gray;
//    overflow: scroll;
}

::-webkit-scrollbar{width:5px;border-left:1px solid #E6ECF8;}
::-webkit-scrollbar-thumb{background-color:#0066ff;}


.table-wrapper{
    margin: 1px 7px 7px;
    box-shadow: 0px 35px 50px rgba( 0, 0, 0, 0.2 );
}

.fl-table {
    border-radius: 5px;
    font-size: 12px;
    font-weight: normal;
    border: none;
    border-collapse: collapse;
    width: 100%;
    max-width: 100%;
    white-space: nowrap;
    background-color: white;
}

.fl-table td, .fl-table th {

    text-align: center;
    padding: 3px;
}

.fl-table td {
    border-right: 1px solid #f8f8f8;
    border-bottom: 1px solid #f8f8f8;
    border-top: 1px solid #f8f8f8;
    border-left: 1px solid #f8f8f8;    
    font-size: 12px;
}

.fl-table thead th {
    
    border-right: 1px solid #f8f8f8;
    border-bottom: 1px solid #f8f8f8;
    border-top: 1px solid #f8f8f8;
    border-left: 1px solid #f8f8f8;
    position: sticky;                   /* fixa o cabecaolho */
    top: 0;                             /* fixa o cabecaolho */
    color: #ffffff;                     /*cor da font das colunas de cabecalho par head */
    background: #0066ff;
    font-size: 14px;
    font-family: cursive; arial;
    font-weight: normal;
}


.fl-table tr:nth-child(even) {
    color: #000000;
    background: #F8F8F8;    /*cor fundo das colunas impares de informacoes  */
}

.fl-table tr:nth-child(odd) {
    color: #000099;
    background: #e0e0e0;    /*cor fundo das colunas pares de informacoes  */
}

/* Responsive */

@media (max-width: 767px) {
    .fl-table {
        display: block;
        width: 100%;
    }
    .table-wrapper:before{
        content: "Scroll horizontally >";
        display: block;
        text-align: right;
        font-size: 11px;
        color: white;
        padding: 0 0 10px;
    }
    .fl-table thead, .fl-table tbody, .fl-table thead th {
        display: block;
    }
    .fl-table thead th:last-child{
        border-bottom: none;
    }
    .fl-table thead {
        float: left;
    }
    .fl-table tbody {
        width: auto;
        position: relative;
        overflow-x: auto;
    }
    .fl-table td, .fl-table th {
        padding: 20px .625em .625em .625em;
        height: 60px;
        vertical-align: middle;
        box-sizing: border-box;
        overflow-x: hidden;
        overflow-y: auto;
        width: 120px;
        font-size: 13px;
        text-overflow: ellipsis;
    }
    .fl-table thead th {
        text-align: left;
        border-bottom: 1px solid #f7f7f9;
    }
    .fl-table tbody tr {
        display: table-cell;
    }
    .fl-table tbody tr:nth-child(odd) {
        background: none;
    }
    .fl-table tr:nth-child(even) {
        background: transparent;
    }
    .fl-table tr td:nth-child(odd) {
        background: #F8F8F8;
        border-right: 1px solid #E6E4E4;
    }
    .fl-table tr td:nth-child(even) {
        border-right: 1px solid #E6E4E4;
    }
    .fl-table tbody td {
        display: block;
        text-align: center;
    }
}

